document.addEventListener("DOMContentLoaded", function() {
    var form = document.getElementById("cadastroForm");

    form.addEventListener("submit", async function(event) {
        event.preventDefault(); // Impede o envio padrão do formulário

        // Obtém os valores dos campos de entrada
        var firstName = document.getElementById("firstname").value;
        var lastName = document.getElementById("lastname").value;
        var email = document.getElementById("email").value;
        var numero = document.getElementById("numero").value;
        var senha = document.getElementById("senha").value;
        var confirmeSenha = document.getElementById("confirme-senha").value;
        var genero = document.querySelector('input[name="genero"]:checked')?.value;

        // Envia os dados ao servidor
        try {
            const response = await fetch('/adicionar', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    firstname: firstName,
                    lastname: lastName,
                    email: email,
                    numero: numero,
                    senha: senha,
                    genero: genero
                })
            });

            if (response.ok) {
                alert('Cadastro realizado com sucesso!');
                window.location.href = "tela2/outraTela.html"; // Redireciona para outra página
            } else {
                alert('Erro ao realizar cadastro!');
            }
        } catch (error) {
            console.error('Erro ao enviar dados', error);
            alert('Erro ao enviar dados. Tente novamente mais tarde.');
        }
    });
});